/**
 * @brief Vypočítá hash pro klíč do LL tabulky.
 */
size_t LLtable_hashFunction(char *key) {
    size_t hash = 5381;
    for(char *i = key; *i; i++) {
        hash = ((hash << 5) + hash) + (unsigned char)(*i);
    }
    return hash;
} // LLtable_hashFunction()

/**
 * @brief Najde pravidlo v LL tabulce na základě neterminálu a řetězce.
 */
int LLtable_findRule(int nt, char *str) {
    // Získání hash hodnoty pro vyhledávaný řetězec
    size_t hash = LLtable_hashFunction(str);

    // Nastavení indexů pro binární vyhledávání
    int left = 0;
    int right = TERMINAL_COUNT - 1;
    int mid;

    // Binární vyhledávání
    while(left <= right) {
        mid = (left + right) / 2;

        // Pokud najdeme odpovídající hash, vrátíme hodnotu pravidla pro daný neterminál
        if(table[mid].key == hash) {
            // Pokud pravidlo neexistuje, došlo k syntaktické chybě
            if(table[mid].value[nt] == SYNTAX_ERROR) {
                error_handle(ERROR_SYNTAX);
            }
            // Jinak pravidlo existuje a vrátímě ho
            else {
                return table[mid].value[nt];
            }
        }

        if(table[mid].key < hash) {
            // Hledaný hash je vyšší, posouváme levý index doprava
            left = mid + 1;
        }
        else {
             // Hledaný hash je nižší, posouváme pravý index doleva
            right = mid - 1;
        }
    }

    // Vrací interní chybový kód, pokud terminál není nalezen v tabulce
    error_handle(ERROR_INTERNAL);
    return ERROR_INTERNAL;
} // LLtable_findRule()

